(()=>{document.addEventListener("DOMContentLoaded",()=>{let e=document.getElementById("navigationToggle"),t=document.getElementById("navigationMenu");if(!e||!t){console.error("menuButton or navMenu not found");return}e.addEventListener("click",()=>{t.hasAttribute("hidden")===!1?(t.setAttribute("hidden",""),e.setAttribute("aria-expanded","false")):(t.removeAttribute("hidden"),e.setAttribute("aria-expanded","true"))}),alert("Navigation script loaded")});})();
